// Add your package belowpackage com.example.android.justjava;import android.content.Intent;import android.net.Uri;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.CheckBox;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;/** * This app displays an order form to order coffee. */public class MainActivity extends AppCompatActivity {    int quantity = 2;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);    }    /**     * This method is called when  + button is clicked.     */    public void increment(View view) {        if (quantity == 100) {            Toast.makeText(this, "You cannot have more than 100 coffees, sorry.", Toast.LENGTH_SHORT).show();            return;        }        quantity = quantity + 1;        displayQuantity(quantity);    }    /**     * This method is called when the - button is clicked.     */    public void decrement(View view) {        if (quantity == 1) {            Toast.makeText(this, "You cannot have less than 1 coffee, sorry.", Toast.LENGTH_SHORT).show();            return;        }        quantity = quantity - 1;        displayQuantity(quantity);    }    /**     * This method is called when the order button is clicked.     */    public void submitOrder(View view) {        EditText nameField = (EditText) findViewById(R.id.name_field);        String name = nameField.getText().toString();        CheckBox whippedCreamCheckBox = (CheckBox) findViewById(R.id.whipped_cream_checkbox);        boolean hasWhippedCream = whippedCreamCheckBox.isChecked();        CheckBox chocolateCheckBox = (CheckBox) findViewById(R.id.chocolate_checkbox);        boolean hasChocolate = chocolateCheckBox.isChecked();        CheckBox cocoaCheckBox = (CheckBox) findViewById(R.id.cocoa_checkbox);        boolean hasCocoa = cocoaCheckBox.isChecked();        int price = calculatePrice(hasWhippedCream, hasChocolate, hasCocoa);        String priceMessage = createOrderSummary(price, name, hasWhippedCream, hasChocolate, hasCocoa);        Intent intent = new Intent(Intent.ACTION_SENDTO);        intent.setData(Uri.parse("mailto:")); // only email apps should handle this        intent.putExtra(Intent.EXTRA_SUBJECT, "Just Java order for " + name);        intent.putExtra(Intent.EXTRA_TEXT, priceMessage);        if (intent.resolveActivity(getPackageManager()) != null) {            startActivity(intent);        }    }    /**     * Calculates the price of the order.     *     * @param addWhippedCream is whether or not the user wants whipped cream topping     */    private int calculatePrice(boolean addWhippedCream, boolean addChocolate, boolean addCocoa) {        int basePrice = 5;        if (addWhippedCream) {            basePrice = basePrice + 1;        }        if (addChocolate) {            basePrice = basePrice + 2;        }        if (addCocoa) {            basePrice = basePrice + 1;        }        return quantity * basePrice;    }    private String createOrderSummary(int price, String name, boolean addWhippedCream, boolean addChocolate, boolean addCocoa) {        String priceMessage = getString(R.string.name_message, name);        priceMessage += "\n" + getString(R.string.add_whipped_cream) + addWhippedCream;        priceMessage += "\n" + getString(R.string.add_choc) + addChocolate;        priceMessage += "\n" + getString(R.string.add_cocoa) + addCocoa;        priceMessage += "\n" + getString(R.string.quantity_message) + quantity;        priceMessage += "\n" + getString(R.string.total_message)+ price;        priceMessage += "\n" + getString(R.string.thank_you);        return priceMessage;    }    /**     * This method displays the given quantity value on the screen.     */    private void displayQuantity(int number) {        TextView quantityTextView = (TextView) findViewById(R.id.quantity_text_view);        quantityTextView.setText("" + number);    }}